import React, { useState, useEffect } from "react";
import { useCart } from "../context/cart";
import axios from "axios";
import Layout from "../components/Layout/Layout";
import { AiOutlineReload } from "react-icons/ai";
import { useTranslation } from "react-i18next";
import { Card } from "antd";

const BlogPage = () => {
  const { t } = useTranslation();
  const [cart, setCart] = useCart();
  const [blogs, setBlogs] = useState([]); // Provide an initial value of an empty array
  const [loading, setLoading] = useState(false);

 

  const getAllBlogs = async () => {
    try {
      const { data } = await axios.get("/api/v1/post/get-post");
      setBlogs(data.blogs);
    } catch (error) {
      console.log(error);
      toast.error("Someething Went Wrong");
    }
  };

  useEffect(() => {
    getAllBlogs();
  }, []);
  return (
    <Layout>
      <div className="row dashboard">
      <div className="col-md-9 ">
          <h1 className="text-center">All blogs List</h1>
          <div className="d-flex flex-wrap">
            {posts?.map((p) => (
              <Link
                key={p._id}
                to={`/dashboard/admin/post/${p.slug}`}
                className="product-link"
              >
                <div className="card m-2" style={{ width: "18rem" }}>
                  <img
                    src={`/api/v1/post/post-photo/${p._id}`}
                    className="card-img-top"
                    alt={p.title}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{p.title}</h5>
                    <p className="card-text">{p.summery}</p>
                    <p className="card-text">{p.content}</p>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
