/* Import the blogdetail.css file */
import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import "../styles/ProductDetailsStyles.css";
import "../styles/blogdetail.css"; /* Add the import statement for blogdetail.css */
import { useCart } from "../context/cart";
import '..'
const BlogDetails = () => {
  const { t } = useTranslation();
  const params = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState({});
  const [cart, setCart] = useCart();

  useEffect(() => {
    if (params?.slug) getPost();
  }, [params?.slug]);

  const getPost = async () => {
    try {
      const { data } = await axios.get(`/api/v1/post/get-post/${params.slug}`);
      setPost(data?.post);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Layout>
      <div className="row container product-details">
        <div className="col-md-6">
          <h3>{post.title}</h3>
          <div>
            <img
              src={`/api/v1/post/post-photo/${post._id}`}
              className="card-img-top"
              alt={post.title}
              height="300"
              width="80%" /* Set the width to 80% */
            />
          </div>

          <div>
            <p>Content: {post.content}</p>
          </div>

          <button
            className="btn btn-secondary ms-1"
            onClick={() => navigate(-1)}
          >
            Back
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default BlogDetails;
