import React, { useState, useEffect } from "react";
import { Checkbox, Radio } from "antd";
import { Prices } from "../components/Prices";
import axios from "axios";
import Layout from "../components/Layout/Layout";

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [checked, setChecked] = useState([]);
  const [radio, setRadio] = useState([]);

  // Fetch categories
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("/api/v1/category/get-category");
        setCategories(response.data.category);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);

  // Fetch products
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/v1/product/product-list");
        setProducts(response.data.products);
      } catch (error) {
        console.error(error);
      }
    };

    fetchProducts();
  }, []);

  // Filter products
  useEffect(() => {
    const filterProducts = async () => {
      try {
        const response = await axios.post("/api/v1/product/product-filters", {
          checked,
          radio,
        });
        setProducts(response.data.products);
      } catch (error) {
        console.error(error);
      }
    };

    filterProducts();
  }, [checked, radio]);

  // Handle category filter
  const handleCategoryFilter = (value, id) => {
    if (value) {
      setChecked((prevChecked) => [...prevChecked, id]);
    } else {
      setChecked((prevChecked) => prevChecked.filter((c) => c !== id));
    }
  };

  // Handle price filter
  const handlePriceFilter = (value) => {
    setRadio(value);
  };

  return (
    <Layout title="All Products - Best offers">
      <div className="container">
        <div className="row">
          <div className="col-md-3">
            <h4>Categories</h4>
            <div>
              {categories.map((category) => (
                <Checkbox
                  key={category._id}
                  onChange={(e) => handleCategoryFilter(e.target.checked, category._id)}
                >
                  {category.name}
                </Checkbox>
              ))}
            </div>
            <h4>Price</h4>
            <div>
              <Radio.Group onChange={(e) => handlePriceFilter(e.target.value)}>
                {Prices.map((price) => (
                  <Radio key={price._id} value={price.array}>
                    {price.name}
                  </Radio>
                ))}
              </Radio.Group>
            </div>
          </div>
          <div className="col-md-9">
            <div className="row">
              {products.map((product) => (
                <div key={product._id} className="col-md-4">
                  <img src={product.image} alt={product.name} />
                  <h5>{product.name}</h5>
                  <p>{product.price}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
