import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { useState } from "react";
import { Navigate } from "react-router-dom";
import Editor from "../Editor";
import axios from "axios";
import { toast } from "react-toastify";

export default function CreatePost() {
  const [title, setTitle] = useState("");
  const [summary, setSummary] = useState("");
  const [content, setContent] = useState("");
  const [photo, setPhoto] = useState("");
  const [redirect, setRedirect] = useState(false);

  

  const getAllCategory = () => {
    // Implement your logic for getting all categories here
  };

  // if (redirect) {
  //   return <Navigate to={'/'} />
  // }

  //create product function
  const createNewPost = async (e) => {
    e.preventDefault();
    try {
      const postData = new FormData();
      postData.append("title", title);
      postData.append("summary", summary);
      postData.append("content", content);
      postData.append("photo", photo);
     const { data } = axios.post(
        "/api/v1/post/create-post",
        postData
      );
      if (data?.success) {
        toast.error(data?.message);
      } else {
        toast.success("Post Created Successfully");
        navigate("/dashboard/admin/posts");
      }
    } catch (error) {
      console.log(error);
      toast.error("something went wrong");
    }
  };




  return (
    <div className="container">
      <h2 className="mt-5 mb-4">Create Post</h2>
      <form onSubmit={createNewPost}>
        <div className="row">
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Title"
              value={title}
              onChange={(ev) => setTitle(ev.target.value)}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Summary"
              value={summary}
              onChange={(ev) => setSummary(ev.target.value)}
            />
          </div>
        </div>
        <div className="mb-3">
                <label className="btn btn-outline-secondary col-md-12">
                  {photo ? photo.name : "UploadPhoto"}
                  <input
                    type="file"
                    name="photo"
                    accept="image/*"
                    onChange={(e) => setPhoto(e.target.files[0])}
                    hidden
                  />
                </label>
              </div>
              <div className="mb-3">
                {photo && (
                  <div className="text-center">
                    <img
                      src={URL.createObjectURL(photo)}
                      alt="product_photo"
                      height={"200px"}
                      className="img img-responsive"
                    />
                  </div>
                )}
              </div>
        <div className="row">
          <div className="col-md-6 mb-3">
            <Editor value={content} onChange={setContent} />
          </div>
        </div>
        <div className="row">
          <div className="col-md-6 mb-3">
            <button className="btn btn-primary">Create post</button>
          </div>
        </div>
      </form>
    </div>
  );
}
