import React, { useState, useEffect } from "react";
import { useCart } from "../context/cart";
import axios from "axios";
import Layout from "../components/Layout/Layout";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";
import { toast } from "react-hot-toast";
import { formatISO9075 } from "date-fns";

const BlogPage = () => {
  const { t } = useTranslation();
  const [cart, setCart] = useCart();
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);

  const getAllBlogs = async () => {
    try {
      const { data } = await axios.get("/api/v1/post/get-post");
      setPosts(data.posts);
    } catch (error) {
      console.log(error);
      toast.error("Something Went Wrong");
    }
  };

  useEffect(() => {
    getAllBlogs();
  }, []);

  return (
    <Layout>
      <div className="row dashboard">
        <div className="col-md-9">
          <h1 className="text-center">All blogs List</h1>
          <div className="d-flex flex-wrap">
            {posts.map((p) => (
              <Link
                key={p._id}
                to={`/dashboard/admin/post/${p.slug}`}
                className="product-link"
              >
                <div className="image">
                  <Link to={`/post/${p._id}`}>
                    <img src={`/api/v1/post/post-photo/${p._id}`} alt={p.title} />
                  </Link>
                </div>
                <div className="texts">
                  <Link to={`/post/${p._id}`}>
                    <h2>{p.title}</h2>
                  </Link>
                  <p className="info">
                
                    <time>{formatISO9075(new Date(p.createdAt))}</time>
                  </p>
                  <p className="summary">{p.summery}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
