import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import "../styles/ProductDetailsStyles.css";
import { useCart } from "../context/cart";
import toast from "react-hot-toast";
const DropShippingProductDetails = () => {
  const { t } = useTranslation();
  const params = useParams();
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();
  const [dropShippingProduct, setProduct] = useState({});
  const [relatedDropShippingProducts, setRelatedDropShippingProducts] = useState([]);
  const [cart, setCart] = useCart();
  //initalp details
  useEffect(() => {
    if (params?.slug) getDropShippingProduct();
    getTotal();
  }, [params?.slug]);

  //get all total products
  const getTotal = async () => {
    try {
      const { data } = await axios.get("/api/v1/dropShippingProduct/dropShippingProduct-count");
      setTotal(data?.total);
    } catch (error) {
      console.log(error);
    }
  };

  //getProduct
  const getDropShippingProduct = async () => {
    try {
      const { data } = await axios.get(
        `/api/v1/dropShippingProduct/get-dropShippingProduct/${params.slug}`
      );
      setDropShippingProduct(data?.dropShippingProduct);
      getSimilarDropShippingProduct(data?.dropShippingProduct._id, data?.dropShippingProduct.category._id);
    } catch (error) {
      console.log(error);
    }
  };
  //get similar product
  const getSimilarDropShippingProduct = async (pid, cid) => {
    try {
      const { data } = await axios.get(
        `/api/v1/dropShippingProduct/related-dropShippingProduct/${pid}/${cid}`
      );
      setRelatedDropShippingProducts(data?.dropShippingProducts);
    } catch (error) {
      console.log(error);
    }
  };
  
  return (
    <Layout>
      <div className="row container product-details">
        <div className="col-md-6">
          <img
            src={`/api/v1/dropShippingProduct/dropShippingProduct-photo/${dropShippingProduct._id}`}
            className="card-img-top"
            alt={dropShippingProduct.name}
            height="300"
            width={"350px"}
          />
        </div>
        <div className="col-md-6 product-details-info">
          <h1 className="text-center">{t("ProductDetail")}</h1>
          <hr />
          <h6>{t("Name")} : {dropShippingProduct.name}</h6>
          <h6>{t("Description")} : {dropShippingProduct.description}</h6>
          <h6>
          {t("Price")} :
            {product?.price?.toLocaleString("en-US", {
              style: "currency",
              currency: "USD",
            })}
          </h6>
          <h6>{t("Category")} : {dropShippingProduct?.category?.name}</h6>
          <button class="btn btn-secondary ms-1">{t("ADDTOCART")}</button>
        </div>
      </div>
      <hr />
      <div className="row container similar-products">
        <h4>{t("SimilarProduct")} ➡️</h4>
        {relatedDropShippingProducts.length < 1 && (
          <p className="text-center">{t("SimilarProduct")}</p>
        )}
        <div className="d-flex flex-wrap">
          {relatedProducts?.map((p) => (
            <div className="card m-2" key={p._id}>
              <img
                src={`/api/v1/dropShippingProduct/dropShippingProduct-photo/${p._id}`}
                className="card-img-top"
                alt={p.name}
              />
              <div className="card-body">
                <div className="card-name-price">
                  <h5 className="card-title">{p.name}</h5>
                  <h5 className="card-title card-price">
                    {p.price.toLocaleString("en-US", {
                      style: "currency",
                      currency: "USD",
                    })}
                  </h5>
                </div>
                <p className="card-text ">
                  {p.description.substring(0, 60)}...
                </p>
                <div className="card-name-price">
                  <button
                    className="btn btn-info ms-1"
                    onClick={() => navigate(`/dropShippingProduct/${p.slug}`)}
                  >
                    {t("NoSimilar")}
                  </button>
                  <button
                  className="btn btn-dark ms-1"
                  onClick={() => {
                    setCart([...cart, p]);
                    localStorage.setItem(
                      "cart",
                      JSON.stringify([...cart, p])
                    );
                    toast.success("Item Added to cart");
                  }}
                >
                  ADD TO CART
                </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default DropShippingProductDetails;
