import React, { useState, useEffect } from "react";
import AdminMenu from "../../components/Layout/AdminMenu";
import Layout from "./../../components/Layout/Layout";
import axios from "axios";
import toast from "react-hot-toast";
import { Link } from "react-router-dom";



const Users = () => {
  const [users, setUsers] = useState([]);

  //getall products
  const getAllUsers = async () => {
    try {
      const { data } = await axios.get("/api/v1/Users/get-user");
      setUsers(data.users);
    } catch (error) {
      console.log(error);
      toast.error("Someething Went Wrong");
    }
  };

  //lifecycle method
  useEffect(() => {
    getAllUsers();
  }, []);

  return (
    <Layout title={"Dashboard - All Users"}>
      <div className="container-fluid m-3 p-3">
        <div className="row">
          <div className="col-md-3">
            <AdminMenu />
          </div>
          <div className="col-md-9">
            <h1>All Users</h1>

            <div className="d-flex flex-wrap">
            {users?.map((p) => (
              <Link
                key={p._id}
                to={`/dashboard/admin/user/${p.slug}`}
                className="product-link"
              >

                  <div className="card-body">
                    <h5 className="card-title">{p.name}</h5>
                    <p className="card-text">{p.email}</p>
                    <p className="card-text">{p.phone}</p>
                    <p className="card-text">{p.address}</p>
                    <p className="card-text">{p.role}</p>
                  </div>

              </Link>
            ))}
          </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Users;
