import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/cart";
import axios from "axios";
import toast from "react-hot-toast";
import Layout from "../components/Layout/Layout";
import { AiOutlineReload } from "react-icons/ai";
import { useTranslation } from "react-i18next";
import { Card } from "antd";

const BlogPage = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [cart, setCart] = useCart();
  const [blogs, setBlogs] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    getAllBlogs();
  }, [page]);

  const getAllBlogs = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get(`/api/v1/post/post-list/${page}`);
      setLoading(false);
      setBlogs((prevBlogs) => [...prevBlogs, ...data.blogs]);
      setTotal(data.total);
    } catch (error) {
      setLoading(false);
      console.log(error);
    }
  };

  return (
    <Layout title={"All Blogs - Best offers"}>
      {/* banner image */}
      <div className="container-fluid row mt-3 home-page">
        <div className="col-md-9">
          <h1 className="text-center">All Blogs</h1>
          <div className="d-flex flex-wrap">
            {blogs.map((p) => (
              <div key={p._id} className="card m-2" style={{ width: "18rem" }}>
                <img
                  src={`/api/v1/post/post-photo/${p._id}`}
                  className="card-img-top"
                  alt={p.title}
                />
                <div className="card-body">
                  <h5 className="card-title">{p.title}</h5>
                  <p className="card-text">{p.summery}</p>
                  <p className="card-text">{p.content}</p>
                </div>
              </div>
            ))}
          </div>
          <div className="m-2 p-3">
            {blogs.length < total && (
              <button
                className="btn loadmore"
                onClick={() => setPage((prevPage) => prevPage + 1)}
              >
                {loading ? (
                  "Loading ..."
                ) : (
                  <>
                    {t("translation:Loadmore")} <AiOutlineReload />
                  </>
                )}
              </button>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default BlogPage;
