import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import "../styles/ProductDetailsStyles.css";
import { useCart } from "../context/cart";


const BlogDetails = () => {
  const { t } = useTranslation();
  const params = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState({});
  const [cart, setCart] = useCart();

  useEffect(() => {
    if (params?.slug) getPost();
  }, [params?.slug]);

  const getPost = async () => {
    try {
      const { data } = await axios.get(`/api/v1/post/get-post/${params.slug}`);
      setPost(data?.post);
    } catch (error) {
      console.log(error);
    }
  };
  
return (
    <Layout>
      <div className="row container product-details">
        <div className="col-md-6">
        <h6>Title: {post.title}</h6>
          <img
            src={`/api/v1/post/post-photo/${post._id}`}
            className="card-img-top"
            alt={post.title}
            height="300"
            width="350px"
          />
        </div>
     
        
    
          <h6>Summary: {post.summery}</h6> {/* Corrected spelling of `Summary` */}
          <h6>Content: {post.content}</h6>
          <button className="btn btn-secondary ms-1" onClick={() => navigate(-1)}>
            Back
          </button> {/* Corrected `class` to `className` and added onClick event */}
    
      </div>
      <hr />
    </Layout>
  );
};

export default BlogDetails;
