import React, { useState } from "react";
import Layout from "./../../components/Layout/Layout";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import "../../styles/AuthStyles.css";
import { useTranslation } from "react-i18next";
const ForgotPasssword = () => {
  const { t } = useTranslation();
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [answer, setAnswer] = useState("");

  const navigate = useNavigate();

  // form function
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/api/v1/auth/forgot-password", {
        email,
        newPassword,
        answer,
      });
      if (res && res.data.success) {
        toast.success(res.data && res.data.message);

        navigate("/login");
      } else {
        toast.error(res.data.message);
      }
    } catch (error) {
      console.log(error);
      toast.error("Something went wrong");
    }
  };
  return (
    <Layout title={t("forgotPassword:title")}>
      <div className="form-container ">
        <form onSubmit={handleSubmit}>
          <h4 className="title">{t("forgotPassword:RESETPASSWORD")}</h4>

          <div className="mb-3">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              id="exampleInputEmail1"
              placeholder={t("forgotPassword:YourEmail")}
              required
            />
          </div>
          {/* <div className="mb-3">
            <input
              type="text"
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              className="form-control"
              id="exampleInputEmail1"
              placeholder={t("forgotPassword:favoriteSport")}
              required
            />
          </div> */}
          <div className="mb-3">
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="form-control"
              id="exampleInputPassword1"
              placeholder={t("forgotPassword:YourPassword")}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary">
          {t("forgotPassword:RESET")}
          </button>
        </form>
      </div>
    </Layout>
  );
};

export default ForgotPasssword;
