import React, { useEffect, useState } from "react";
import { Navigate, useParams, useNavigate } from "react-router-dom";
import Editor from "../Editor";
import axios from "axios";
import { toast } from "react-toastify";
import AdminMenu from "./../../components/Layout/AdminMenu";
export default function EditPost() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [summary, setSummary] = useState("");
  const [content, setContent] = useState("");
  const [photo, setPhoto] = useState(null);
  const [redirect, setRedirect] = useState(false);

  const getSinglePost = async () => {
    try {
      const { data } = await axios.get(`/api/v1/post/get-post/${id}`);
      setTitle(data.post.title);
      setContent(data.post.content);
      setSummary(data.post.summary);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getSinglePost();
  }, []);

  const handleDelete = async () => {
    try {
      let answer = window.prompt("Are You Sure want to delete this post? ");
      if (!answer) return;
      await axios.delete(`/api/v1/post/delete-post/${id}`);
      toast.success("Product Deleted Successfully");
      navigate("/dashboard/admin/posts");
    } catch (error) {
      console.log(error);
      toast.error("Something went wrong");
    }
  };

  const handlePost = async (e) => {
    e.preventDefault();

    try {
      const postData = new FormData();
      postData.append("title", title);
      postData.append("summary", summary);
      postData.append("content", content);
      photo && postData.append("photo", photo);

      const { data } = await axios.put(
        `/api/v1/post/update-post/${id}`,
        postData
      );

      if (data?.success) {
        toast.error(data?.message);
      } else {
        toast.success("Post Updated Successfully");
        navigate("/dashboard/admin/posts");
      }
    } catch (error) {
      console.log(error);
      toast.error("something went wrong");
    }
  };

  if (redirect) {
    return <Navigate to={"/post/" + id} />;
  }

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4">
        <h2 className="text-center mb-4">Update Post</h2>
        <form onSubmit={handlePost}>
        <div className="col-md-3">
            <AdminMenu />
          </div>
          <div className="mb-3">
                <label className="btn btn-outline-secondary col-md-12">
                  {photo ? photo.name : "Upload Photo"}
                  <input
                    type="file"
                    name="photo"
                    accept="image/*"
                    onChange={(e) => setPhoto(e.target.files[0])}
                    hidden
                  />
                </label>
              </div>
              <div className="mb-3">
                {photo ? (
                  <div className="text-center">
                    <img
                      src={URL.createObjectURL(photo)}
                      alt="product_photo"
                      height={"200px"}
                      className="img img-responsive"
                    />
                  </div>
                ) : (
                  <div className="text-center">
                    <img
                      src={`/api/v1/post/product==-photo/${id}`}
                      alt="product_photo"
                      height={"200px"}
                      className="img img-responsive"
                    />
                  </div>
                )}
              </div>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Summary"
              value={summary}
              onChange={(ev) => setSummary(ev.target.value)}
            />
          </div>
          <div className="mb-3">
            <input
              type="file"
              className="form-control"
              onChange={(ev) => setPhoto(ev.target.photo)}
            />
          </div>
          <div className="mb-3">
            <Editor onChange={setContent} value={content} />
          </div>
          <div className="mb-3">
                <button className="btn btn-primary" onClick={handlePost}>
                update Post
                </button>
              </div>
          <div className="mb-3">
            <button className="btn btn-danger" onClick={handleDelete}>
              Delete Post
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
